import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const MeetingSessionPage = () => {
  const { sessionId } = useParams(); // Get session ID from the URL
  const [session, setSession] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch session details
    const fetchSession = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/sessions/${sessionId}`);
        if (response.status === 200) {
          setSession(response.data.session);
        } else {
          setError('Failed to fetch session.');
        }
      } catch (err) {
        setError('Error fetching session.');
        console.error(err);
      }
    };

    fetchSession();
  }, [sessionId]);

  return (
    <div className="session-page">
      {error && <p className="error-message">{error}</p>}
      {session ? (
        <div>
          <h2>{session.name}</h2>
          <p>Date: {new Date(session.session_datetime).toLocaleString()}</p>
          {session.audio_url && (
            <p>
              <a href={session.audio_url} target="_blank" rel="noopener noreferrer">
                Listen to the audio recording
              </a>
            </p>
          )}
        </div>
      ) : (
        !error && <p>Loading session...</p>
      )}
    </div>
  );
};

export default MeetingSessionPage;
