import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const MeetingPage = () => {
  const { meetingId } = useParams(); // Get meeting ID from the URL
  const [meeting, setMeeting] = useState(null);
  const [sessions, setSessions] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch meeting details
    const fetchMeeting = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/meetings?meeting_id=${meetingId}`);
        if (response.status === 200) {
          setMeeting(response.data.meeting);
        } else {
          setError('Failed to fetch meeting');
        }
      } catch (err) {
        setError('Error fetching meeting');
        console.error(err);
      }
    };

    // Fetch sessions for the meeting
    const fetchSessions = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/meetings?meeting_id=${meetingId}`);
        if (response.status === 200) {
          setSessions(response.data.sessions);
        } else {
          setError('Failed to fetch sessions');
        }
      } catch (err) {
        setError('Error fetching sessions');
        console.error(err);
      }
    };

    fetchMeeting();
    fetchSessions();
  }, [meetingId]);

  return (
    <div className="meeting-page">
      {error && <p className="error-message">{error}</p>}
      {meeting && (
        <div>
          <h2>{meeting.name}</h2>
          <p>{meeting.description}</p>
          <h3>Sessions</h3>
          <ul>
            {sessions.map(session => (
              <li key={session.id}>
                {session.name} - {new Date(session.session_datetime).toLocaleString()}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default MeetingPage;
