import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // To get the meeting ID from the URL
import axios from 'axios';
import { Table, Spinner, Alert } from 'react-bootstrap';

const MeetingsPage = () => {
  const { meetingId } = useParams(); // Assuming you're passing the meeting ID via the URL
  const [meetingSessions, setMeetingSessions] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchMeetingSessions = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/meetings/${meetingId}/sessions`);
        setMeetingSessions(response.data.sessions);
      } catch (err) {
        setError('Failed to fetch meeting sessions.');
      } finally {
        setIsLoading(false);
      }
    };

    fetchMeetingSessions();
  }, [meetingId]);

  if (isLoading) {
    return <Spinner animation="border" role="status"><span className="visually-hidden">Loading...</span></Spinner>;
  }

  if (error) {
    return <Alert variant="danger">{error}</Alert>;
  }

  return (
    <div>
      <h2>Meeting Sessions</h2>
      {meetingSessions.length > 0 ? (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Session Name</th>
              <th>Date</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {meetingSessions.map(session => (
              <tr key={session.id}>
                <td>{session.name}</td>
                <td>{new Date(session.date).toLocaleDateString()}</td>
                <td>{new Date(session.date).toLocaleTimeString()}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      ) : (
        <Alert variant="info">No sessions found for this meeting.</Alert>
      )}
    </div>
  );
};

export default MeetingsPage;
