import React, { useEffect, useCallback } from 'react';
import { BrowserRouter as Router, Route, Routes, NavLink } from 'react-router-dom';
import { Container, Row, Col, Form, FormControl, Button, Navbar, Nav } from 'react-bootstrap';
import './styles.css';
import AudioRecorder from './AudioRecorder';
import { RecorderProvider } from './RecorderContext';
import { UserProvider, useUser } from './UserContext';
import DashboardPage from './components/DashboardPage';
import MeetingsPage from './components/MeetingsPage';
import ActionItemsPage from './components/ActionItemsPage';
import SettingsPage from './components/SettingsPage';
import LoginPage from './components/LoginPage';
import SignupPage from './components/SignupPage';
import UserEmailDisplay from './components/UserEmailDisplay';
import axios from 'axios';
import PrivateRoute from './components/PrivateRoute';
import LogoutButton from './components/LogoutButton';

axios.defaults.withCredentials = true; // Ensure cookies are sent with every request

const AppContent = () => {
  const { user, loginUser, logoutUser } = useUser();

  // Log initial load and session status check
  const checkSession = useCallback(async () => {
    console.log("Checking session status...");

    try {
      const response = await axios.get('http://localhost:5000/auth/status');

      if (response.data.logged_in) {
        console.log(`User is logged in as: ${response.data.user.email}`);
        loginUser(response.data.user); // Set user in context if logged in
      } else {
        console.warn("User is not logged in.");
        logoutUser(); // Clear user from context if not logged in
      }
    } catch (error) {
      console.error("Error checking session status:", error);
      logoutUser(); // Clear user from context in case of error
    }
  }, [loginUser, logoutUser]);

  useEffect(() => {
    console.log("AppContent component loaded.");
    checkSession(); // Run session check on mount

    return () => {
      console.log("AppContent component will unmount.");
    };
  }, [checkSession]);

  console.log(user ? `Page loaded with user: ${user.email}` : "Page loaded with no user logged in.");

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage />} />
        <Route path="/signup" element={<SignupPage />} />

        {/* Protect the following routes */}
        <Route element={<PrivateRoute />}>
          <Route
            path="/"
            element={
              <RecorderProvider>
                <div>
                  <Navbar bg="dark" variant="dark" className="mb-4">
                    <Container fluid>
                      <Navbar.Brand as={NavLink} to="/">MinuteMemo</Navbar.Brand>
                      <Form className="d-flex">
                        <FormControl
                          type="search"
                          placeholder="Search"
                          className="mr-2"
                          aria-label="Search"
                        />
                        <Button variant="outline-success">Search</Button>
                      </Form>
                      {user && <LogoutButton />} {/* Show logout button if the user is logged in */}
                    </Container>
                  </Navbar>
                  <Container fluid>
                    <Row>
                      <Col md={3} className="bg-light sidebar">
                        <Nav defaultActiveKey="/" className="flex-column">
                          <NavLink to="/" className="nav-link">Dashboard</NavLink>
                          <NavLink to="/meetings" className="nav-link">Meetings</NavLink>
                          <NavLink to="/action-items" className="nav-link">Action Items</NavLink>
                          <NavLink to="/settings" className="nav-link">Settings</NavLink>
                        </Nav>
                        <UserEmailDisplay /> {/* Display the user email here */}
                        <AudioRecorder />
                      </Col>
                      <Col md={9} className="content">
                        <Routes>
                          <Route path="/" element={<DashboardPage />} />
                          <Route path="/meetings" element={<MeetingsPage />} />
                          <Route path="/action-items" element={<ActionItemsPage />} />
                          <Route path="/settings" element={<SettingsPage />} />
                        </Routes>
                      </Col>
                    </Row>
                  </Container>
                </div>
              </RecorderProvider>
            }
          />
        </Route>
      </Routes>
    </Router>
  );
};

const App = () => (
  <UserProvider>
    <AppContent />
  </UserProvider>
);

export default App;
